import { useState } from 'react';
import axios from 'axios';
import { Form, Button, Alert } from 'react-bootstrap';

export default function Flights() {
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [date, setDate] = useState('');
  const [error, setError] = useState('');
  const [flights, setFlights] = useState([]);

  const fetchFlights = async (formattedDate) => {
    try {
      const { data } = await axios.get('/api/flights/search', {
        params: { from, to, date: formattedDate },
      });
      setFlights(data);
      setError('');
    } catch (err) {
      setError('Failed to fetch flights.');
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const formattedDate = new Date(date).toISOString().split('T')[0];
    console.log('Formatted Date:', formattedDate);
    fetchFlights(formattedDate);
  };

  return (
    <div className="p-4">
      <h3>Search Flights</h3>
      <Form onSubmit={handleSubmit}>
        <Form.Group className="mb-3" controlId="from">
          <Form.Label>From</Form.Label>
          <Form.Control
            type="text"
            name="from"
            placeholder="Enter departure city"
            value={from}
            onChange={(e) => setFrom(e.target.value)}
            required
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="to">
          <Form.Label>To</Form.Label>
          <Form.Control
            type="text"
            name="to"
            placeholder="Enter destination city"
            value={to}
            onChange={(e) => setTo(e.target.value)}
            required
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="date">
          <Form.Label>Departure Date</Form.Label>
          <Form.Control
            type="date"
            name="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
          />
        </Form.Group>

        <Button type="submit">Search Flights</Button>
      </Form>

      {error && <Alert variant="danger" className="mt-3">{error}</Alert>}

      <div className="mt-4">
        {flights.length > 0 ? (
          flights.map((flight) => (
            <div key={flight._id} className="border p-2 mb-2 rounded">
              <p><strong>{flight.from} to {flight.to}</strong></p>
              <p>Departure: {new Date(flight.date).toDateString()}</p>
              <p>Price: â‚¹{flight.price}</p>
            </div>
          ))
        ) : (
          <p>No flights found for the selected date.</p>
        )}
      </div>
    </div>
  );
}
