import { useState } from 'react';
import axios from 'axios';
import { Form, Button, Alert } from 'react-bootstrap';

export default function Hotels() {
  const [location, setLocation] = useState('');
  const [date, setDate] = useState('');
  const [hotels, setHotels] = useState([]);
  const [error, setError] = useState('');

  const fetchHotels = async (formattedDate) => {
    try {
      const { data } = await axios.get('/api/hotels/search', {
        params: { location, date: formattedDate },
      });
      setHotels(data);
      setError('');
    } catch (err) {
      setError('Failed to fetch hotels.');
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const formattedDate = new Date(date).toISOString().split('T')[0];
    fetchHotels(formattedDate);
  };

  return (
    <div className="p-4">
      <h3>Search Hotels</h3>
      <Form onSubmit={handleSubmit}>
        <Form.Group className="mb-3" controlId="location">
          <Form.Label>City</Form.Label>
          <Form.Control
            type="text"
            name="location"
            placeholder="Enter city"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            required
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="checkin-date">
          <Form.Label>Check-in Date</Form.Label>
          <Form.Control
            type="date"
            name="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
          />
        </Form.Group>

        <Button type="submit">Search Hotels</Button>
      </Form>

      {error && <Alert variant="danger" className="mt-3">{error}</Alert>}

      <div className="mt-4">
        {hotels.length > 0 ? (
          hotels.map((hotel) => (
            <div key={hotel._id} className="border p-2 mb-2 rounded">
              <p><strong>{hotel.name}</strong></p>
              <p>City: {hotel.location}</p>
              <p>Available: {new Date(hotel.date).toDateString()}</p>
              <p>Price: ₹{hotel.price} / night</p>
              <p>Rating: {hotel.rating} ⭐</p>
            </div>
          ))
        ) : (
          <p>No hotels found for the selected date.</p>
        )}
      </div>
    </div>
  );
}
