import { useState } from 'react';
import axios from 'axios';
import { Form, Button, Alert } from 'react-bootstrap';

export default function Flights() {
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [date, setDate] = useState('');
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');
  const [sort, setSort] = useState('');
  const [flights, setFlights] = useState([]);
  const [error, setError] = useState('');

  const fetchFlights = async (formattedDate) => {
    try {
      const { data } = await axios.get('/api/flights/search', {
        params: { from, to, date: formattedDate, minPrice, maxPrice, sort },
      });
      setFlights(data);
      setError('');
    } catch (err) {
      setError('Failed to fetch flights.');
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const formattedDate = new Date(date).toISOString().split('T')[0];
    fetchFlights(formattedDate);
  };

  return (
    <div>
      <Form onSubmit={handleSubmit}>
        <Form.Control type="text" placeholder="From" value={from} onChange={(e) => setFrom(e.target.value)} required />
        <Form.Control type="text" placeholder="To" value={to} onChange={(e) => setTo(e.target.value)} required />
        <Form.Control type="date" value={date} onChange={(e) => setDate(e.target.value)} required />
        
        {/* Filters */}
        <Form.Control type="number" placeholder="Min Price" value={minPrice} onChange={(e) => setMinPrice(e.target.value)} />
        <Form.Control type="number" placeholder="Max Price" value={maxPrice} onChange={(e) => setMaxPrice(e.target.value)} />
        <Form.Select value={sort} onChange={(e) => setSort(e.target.value)}>
          <option value="">Sort by Price</option>
          <option value="asc">Low to High</option>
          <option value="desc">High to Low</option>
        </Form.Select>

        <Button type="submit" className="mt-2">Search Flights</Button>
      </Form>

      {error && <Alert variant="danger">{error}</Alert>}

      <div className="mt-3">
        {flights.length > 0 ? (
          flights.map((flight) => (
            <div key={flight._id}>
              <p>{flight.from} ➡ {flight.to}</p>
              <p>Departure: {flight.date ? new Date(flight.date).toLocaleDateString('en-IN', { weekday: 'short', year: 'numeric', month: 'short', day: 'numeric' }) : "Date Not Available"}</p>

              <p>Price: ₹{flight.price}</p>
            </div>
          ))
        ) : (
          <p>No flights found for the selected date and filters.</p>
        )}
      </div>
    </div>
  );
}
