import { useState } from 'react';
import axios from 'axios';
import { Form, Button, Alert, Card, Row, Col } from 'react-bootstrap';

export default function Flights() {
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [date, setDate] = useState('');
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [flights, setFlights] = useState([]);

  const fetchFlights = async (formattedDate) => {
    try {
      const filters = {
        source: from.trim(),
        destination: to.trim(),
        date: formattedDate,
      };

      if (minPrice !== '') filters.minPrice = minPrice;
      if (maxPrice !== '') filters.maxPrice = maxPrice;

      const { data } = await axios.get('/api/flights/search', { params: filters });
      setFlights(data);
      setError('');
    } catch (err) {
      setError('Failed to fetch flights.');
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const formattedDate = new Date(date).toISOString().split('T')[0];
    fetchFlights(formattedDate);
  };

  const handleBook = async (flight) => {
    const token = localStorage.getItem('token');
    if (!token) return setError('Please login to book.');

    try {
      await axios.post('/api/bookings/flight', {
        flight: flight._id,
        seats: 1,
        totalPrice: flight.price,
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setSuccess('Flight booked successfully!');
    } catch (err) {
      setError('Failed to book flight.');
    }
  };

  return (
    <div className="container mt-5">
      <h2 className="mb-4">✈️ Search Flights</h2>
      <Form onSubmit={handleSubmit}>
        <Row className="g-3 mb-3">
          <Col md={4}>
            <Form.Control
              placeholder="From (e.g., Delhi)"
              value={from}
              onChange={(e) => setFrom(e.target.value)}
              required
            />
          </Col>
          <Col md={4}>
            <Form.Control
              placeholder="To (e.g., Mumbai)"
              value={to}
              onChange={(e) => setTo(e.target.value)}
              required
            />
          </Col>
          <Col md={4}>
            <Form.Control
              type="date"
              value={date}
              onChange={(e) => setDate(e.target.value)}
              required
            />
          </Col>
        </Row>
        <Row className="g-3 mb-4">
          <Col md={6}>
            <Form.Control
              type="number"
              placeholder="Min Price"
              value={minPrice}
              onChange={(e) => setMinPrice(e.target.value)}
            />
          </Col>
          <Col md={6}>
            <Form.Control
              type="number"
              placeholder="Max Price"
              value={maxPrice}
              onChange={(e) => setMaxPrice(e.target.value)}
            />
          </Col>
        </Row>
        <Button type="submit" variant="primary">Search</Button>
      </Form>

      {error && <Alert className="mt-3" variant="danger">{error}</Alert>}
      {success && <Alert className="mt-3" variant="success">{success}</Alert>}

      <div className="mt-4">
        {flights.map((flight) => (
          <Card key={flight._id} className="mb-3 shadow-sm">
            <Card.Body>
              <Card.Title>{flight.airline}</Card.Title>
              <Card.Text>
                {flight.from} ➡ {flight.to} <br />
                Departure: {new Date(flight.departureDate).toDateString()}<br />
                Time: {flight.departureTime} - {flight.arrivalTime}<br />
                Price: ₹{flight.price}
              </Card.Text>
              <Button variant="success" onClick={() => handleBook(flight)}>Book Now</Button>
            </Card.Body>
          </Card>
        ))}
      </div>
    </div>
  );
}
