import { useState } from 'react';
import axios from 'axios';
import { Form, Button, Alert, Container, Row, Col } from 'react-bootstrap';

export default function Flights() {
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [date, setDate] = useState('');
  const [error, setError] = useState('');
  const [flights, setFlights] = useState([]);
  const [searched, setSearched] = useState(false); // To control "no flights found" message

  const fetchFlights = async (formattedDate) => {
    try {
      const { data } = await axios.get('/api/flights/search', {
        params: { from, to, date: formattedDate },
      });
      setFlights(data);
      setError('');
    } catch (err) {
      setError('Failed to fetch flights. Please try again.');
      setFlights([]);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const formattedDate = new Date(date).toISOString().split('T')[0];

    // Log once during submit
    console.log('Searching flights for:', { from, to, formattedDate });

    fetchFlights(formattedDate);
    setSearched(true);
  };

  return (
    <Container className="mt-4">
      <h2>Search Flights</h2>
      <Form onSubmit={handleSubmit}>
        <Row className="mb-3">
          <Col>
            <Form.Control
              type="text"
              placeholder="From"
              value={from}
              onChange={(e) => setFrom(e.target.value)}
              required
            />
          </Col>
          <Col>
            <Form.Control
              type="text"
              placeholder="To"
              value={to}
              onChange={(e) => setTo(e.target.value)}
              required
            />
          </Col>
          <Col>
            <Form.Control
              type="date"
              value={date}
              onChange={(e) => setDate(e.target.value)}
              required
            />
          </Col>
          <Col>
            <Button type="submit" variant="primary">Search</Button>
          </Col>
        </Row>
      </Form>

      {error && <Alert variant="danger">{error}</Alert>}

      <div>
        {flights.length > 0 ? (
          <div>
            <h4>Available Flights:</h4>
            {flights.map((flight) => (
              <div key={flight._id} className="border p-2 mb-2">
                <p><strong>{flight.from}</strong> to <strong>{flight.to}</strong></p>
                <p>Departure: {new Date(flight.departureDate).toDateString()}</p>
                <p>Price: ₹{flight.price}</p>
              </div>
            ))}
          </div>
        ) : (
          searched && <p>No flights found for the selected date.</p>
        )}
      </div>
    </Container>
  );
}
