import { useState } from 'react';
import axios from 'axios';
import { Form, Button, Alert, Card, Row, Col } from 'react-bootstrap';

export default function Hotels() {
  const [location, setLocation] = useState('');
  const [date, setDate] = useState('');
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');
  const [rating, setRating] = useState('');
  const [sortBy, setSortBy] = useState('');
  const [amenities, setAmenities] = useState([]);
  const [hotels, setHotels] = useState([]);
  const [error, setError] = useState('');

  const allAmenities = ['wifi', 'pool', 'spa', 'restaurant', 'parking'];

  const handleAmenityChange = (e) => {
    const { value, checked } = e.target;
    if (checked) {
      setAmenities([...amenities, value]);
    } else {
      setAmenities(amenities.filter((item) => item !== value));
    }
  };

  const fetchHotels = async (formattedDate) => {
    try {
      const filters = {
        location,
        date: formattedDate,
      };
      if (minPrice) filters.minPrice = minPrice;
      if (maxPrice) filters.maxPrice = maxPrice;
      if (rating) filters.rating = rating;
      if (sortBy) filters.sortBy = sortBy;
      if (amenities.length > 0) filters.amenities = amenities;

      const { data } = await axios.get('/api/flights/hotels/search', { params: filters });
      setHotels(data);
      setError('');
    } catch (err) {
      setError('Failed to fetch hotels.');
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const formattedDate = new Date(date).toISOString().split('T')[0];
    fetchHotels(formattedDate);
  };

  return (
    <div className="p-4">
      <h3>Search Hotels</h3>
      <Form onSubmit={handleSubmit} className="mb-4">
        <Form.Control
          type="text"
          placeholder="Location"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          required
          className="mb-2"
        />
        <Form.Control
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          required
          className="mb-2"
        />
        <Form.Control
          type="number"
          placeholder="Min Price"
          value={minPrice}
          onChange={(e) => setMinPrice(e.target.value)}
          className="mb-2"
        />
        <Form.Control
          type="number"
          placeholder="Max Price"
          value={maxPrice}
          onChange={(e) => setMaxPrice(e.target.value)}
          className="mb-2"
        />
        <Form.Control
          type="number"
          placeholder="Min Rating (1-5)"
          value={rating}
          onChange={(e) => setRating(e.target.value)}
          className="mb-2"
        />
        <div className="mb-2">
          <strong>Amenities:</strong><br />
          {allAmenities.map((amenity) => (
            <Form.Check
              inline
              key={amenity}
              type="checkbox"
              label={amenity}
              value={amenity}
              checked={amenities.includes(amenity)}
              onChange={handleAmenityChange}
            />
          ))}
        </div>
        <Form.Select
          value={sortBy}
          onChange={(e) => setSortBy(e.target.value)}
          className="mb-3"
        >
          <option value="">Sort By</option>
          <option value="priceLowToHigh">Price: Low to High</option>
          <option value="priceHighToLow">Price: High to Low</option>
          <option value="ratingHighToLow">Rating: High to Low</option>
        </Form.Select>

        <Button type="submit" variant="primary">Search Hotels</Button>
      </Form>

      {error && <Alert variant="danger">{error}</Alert>}

      {hotels.length > 0 ? (
        <Row xs={1} md={2} lg={3} className="g-4">
          {hotels.map((hotel) => (
            <Col key={hotel._id}>
              <Card className="shadow-sm">
                <Card.Body>
                  <Card.Title>{hotel.name}</Card.Title>
                  <Card.Subtitle className="mb-2 text-muted">{hotel.location}</Card.Subtitle>
                  <Card.Text>
                    <strong>Available:</strong>{' '}
                    {hotel.checkinDate ? new Date(hotel.checkinDate).toDateString() : 'N/A'}
                    <br />
                    <strong>Price:</strong> ‚Çπ{hotel.pricePerNight} / night
                    <br />
                    <strong>Rating:</strong>{' '}
                    {'‚≠ê'.repeat(Math.floor(hotel.rating))} ({hotel.rating || 'N/A'})
                    <br />
                    <strong>Amenities:</strong>{' '}
                    {Array.isArray(hotel.amenities)
                      ? hotel.amenities.join(', ')
                      : 'Not specified'}
                  </Card.Text>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      ) : (
        <p>No hotels found for the selected filters.</p>
      )}
    </div>
  );
}
