import { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, Alert, Form } from 'react-bootstrap';

export default function Hotels() {
  const [hotels, setHotels] = useState([]);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [checkIn, setCheckIn] = useState('');
  const [checkOut, setCheckOut] = useState('');

  useEffect(() => {
    const fetchHotels = async () => {
      try {
        const res = await axios.get('/api/hotels');
        setHotels(res.data);
      } catch (err) {
        setError('Failed to load hotels.');
      }
    };
    fetchHotels();
  }, []);

  const handleBook = async (hotel) => {
    const token = localStorage.getItem('authToken');
    if (!token) {
      setError('You must be logged in to book a hotel.');
      return;
    }

    if (!checkIn || !checkOut) {
      setError('Please select check-in and check-out dates.');
      return;
    }

    try {
      const totalDays = (new Date(checkOut) - new Date(checkIn)) / (1000 * 60 * 60 * 24);
      const totalPrice = totalDays * hotel.pricePerNight;

      const bookingData = {
        hotel: hotel._id,
        checkIn,
        checkOut,
        totalPrice,
      };

      await axios.post('/api/hotel-bookings', bookingData, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setSuccess('Hotel booked successfully!');
      setError('');
    } catch (err) {
      setError('Hotel booking failed.');
    }
  };

  return (
    <div className="p-4">
      <h3>Available Hotels</h3>

      <Form className="mb-3">
        <Form.Control
          type="date"
          value={checkIn}
          onChange={(e) => setCheckIn(e.target.value)}
          className="mb-2"
        />
        <Form.Control
          type="date"
          value={checkOut}
          onChange={(e) => setCheckOut(e.target.value)}
          className="mb-2"
        />
      </Form>

      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">{success}</Alert>}

      {hotels.map((hotel) => (
        <div key={hotel._id} className="border p-3 mb-3 rounded">
          <h5>{hotel.name}</h5>
          <p>Location: {hotel.location}</p>
          <p>Price per night: ₹{hotel.pricePerNight}</p>
          <p>Rating: {hotel.starRating} ★</p>
          <Button onClick={() => handleBook(hotel)} variant="success">Book Hotel</Button>
        </div>
      ))}
    </div>
  );
}
