import { useState } from 'react';
import axios from 'axios';
import { Form, Button, Alert } from 'react-bootstrap';

export default function Hotels() {
  const [location, setLocation] = useState('');
  const [date, setDate] = useState('');
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');
  const [rating, setRating] = useState('');
  const [amenities, setAmenities] = useState([]);
  const [sortBy, setSortBy] = useState('');
  const [hotels, setHotels] = useState([]);
  const [error, setError] = useState('');

  const handleAmenityChange = (e) => {
    const value = e.target.value;
    setAmenities((prev) =>
      prev.includes(value) ? prev.filter((a) => a !== value) : [...prev, value]
    );
  };

  const fetchHotels = async (formattedDate) => {
    try {
      const filters = {
        location,
        date: formattedDate,
      };
      if (minPrice) filters.minPrice = minPrice;
      if (maxPrice) filters.maxPrice = maxPrice;
      if (rating) filters.rating = rating;
      if (amenities.length > 0) filters.amenities = amenities.join(',');
      if (sortBy) filters.sortBy = sortBy;

      const { data } = await axios.get('/api/hotels/search', { params: filters });
      setHotels(data);
      setError('');
    } catch (err) {
      console.error(err);
      setError('Failed to fetch hotels.');
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const formattedDate = new Date(date).toISOString().split('T')[0];
    fetchHotels(formattedDate);
  };

  return (
    <div className="p-4">
      <h3>Search Hotels</h3>
      <Form onSubmit={handleSubmit} className="mb-4">
        <Form.Control
          type="text"
          placeholder="Location"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          required
          className="mb-2"
        />
        <Form.Control
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          required
          className="mb-2"
        />
        <Form.Control
          type="number"
          placeholder="Min Price"
          value={minPrice}
          onChange={(e) => setMinPrice(e.target.value)}
          className="mb-2"
        />
        <Form.Control
          type="number"
          placeholder="Max Price"
          value={maxPrice}
          onChange={(e) => setMaxPrice(e.target.value)}
          className="mb-2"
        />

        <Form.Control
          type="number"
          placeholder="Minimum Rating (1 to 5)"
          value={rating}
          onChange={(e) => setRating(e.target.value)}
          className="mb-2"
        />

        <div className="mb-2">
          <label>Amenities:</label>
          <div className="d-flex flex-wrap gap-2">
            {['wifi', 'pool', 'spa', 'restaurant', 'parking'].map((a) => (
              <Form.Check
                key={a}
                type="checkbox"
                label={a}
                value={a}
                checked={amenities.includes(a)}
                onChange={handleAmenityChange}
              />
            ))}
          </div>
        </div>

        <Form.Select value={sortBy} onChange={(e) => setSortBy(e.target.value)} className="mb-3">
          <option value="">Sort By</option>
          <option value="priceAsc">Price: Low to High</option>
          <option value="priceDesc">Price: High to Low</option>
          <option value="rating">Rating</option>
        </Form.Select>

        <Button type="submit" variant="primary">Search Hotels</Button>
      </Form>

      {error && <Alert variant="danger">{error}</Alert>}

      {hotels.length > 0 ? (
        hotels.map((hotel) => (
          <div key={hotel._id} className="border rounded p-3 mb-3">
            <h5>{hotel.name}</h5>
            <p>City: {hotel.location}</p>
            <p>Available: {hotel.checkinDate ? new Date(hotel.checkinDate).toDateString() : 'Date Not Available'}</p>
            <p>Price: ₹{hotel.pricePerNight} / night</p>
            <p>Rating: {hotel.rating} ⭐</p>
            {hotel.amenities && (
              <p>Amenities: {hotel.amenities.join(', ')}</p>
            )}
          </div>
        ))
      ) : (
        <p>No hotels found for the selected filters.</p>
      )}
    </div>
  );
}
