import { useState } from 'react';
import axios from 'axios';
import { Form, Button, Alert } from 'react-bootstrap';

export default function Hotels() {
  const [checkInDate, setCheckInDate] = useState('');
  const [hotels, setHotels] = useState([]);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const fetchHotels = async () => {
    try {
      const { data } = await axios.get('/api/hotels/search', {
        params: { date: checkInDate }
      });
      setHotels(data);
      setError('');
    } catch (err) {
      setError('Failed to load hotels.');
    }
  };

  const handleBook = async (hotel) => {
    const token = localStorage.getItem('authToken');
    if (!token) {
      setError('You must be logged in to book a hotel.');
      return;
    }

    try {
      const { data } = await axios.post('/api/bookings/hotel', {
        hotel: hotel._id,
        totalPrice: hotel.pricePerNight,
      }, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setSuccess('Hotel booked successfully!');
    } catch (err) {
      setError('Hotel booking failed.');
    }
  };

  return (
    <div className="p-4">
      <h3>Available Hotels</h3>
      <Form onSubmit={(e) => { e.preventDefault(); fetchHotels(); }} className="mb-4">
        <Form.Control
          type="date"
          value={checkInDate}
          onChange={(e) => setCheckInDate(e.target.value)}
          required
          className="mb-2"
        />
        <Button type="submit">Search Hotels</Button>
      </Form>

      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">{success}</Alert>}

      {hotels.length > 0 ? (
        hotels.map(hotel => (
          <div key={hotel._id} className="border rounded p-3 mb-3">
            <h5>{hotel.name}</h5>
            <p>Rating: {hotel.rating} ⭐</p>
            <p>Price: ₹{hotel.pricePerNight} per night</p>
            <p>Amenities: {hotel.amenities.join(', ')}</p>
            <Button variant="success" onClick={() => handleBook(hotel)}>Book Now</Button>
          </div>
        ))
      ) : (
        checkInDate && <p>No hotels found for selected date.</p>
      )}
    </div>
  );
}
