import React, { useState } from 'react';
import axios from 'axios';

const Hotels = () => {
  const [location, setLocation] = useState('');
  const [checkinDate, setCheckinDate] = useState('');
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');
  const [rating, setRating] = useState('');
  const [amenities, setAmenities] = useState('');
  const [sortBy, setSortBy] = useState('');
  const [hotels, setHotels] = useState([]);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleSearch = async () => {
    try {
      const { data } = await axios.get('/api/hotels/search', {
        params: {
          location,
          checkinDate,
          minPrice,
          maxPrice,
          rating,
          amenities,
          sortBy
        }
      });
      setHotels(data);
      setError('');
    } catch (err) {
      setHotels([]);
      setError('No hotels found for selected filters.');
    }
  };

  const handleBook = async (hotel) => {
    const token = localStorage.getItem('token');

    if (!token) {
      setError('You must be logged in to book a hotel.');
      return;
    }

    try {
      await axios.post('/api/bookings/hotel', {
        hotel: hotel._id,
        totalPrice: hotel.price
      }, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      setSuccess('Hotel booked successfully!');
      setError('');
    } catch (err) {
      setError('Hotel booking failed.');
      setSuccess('');
    }
  };

  return (
    <div className="container mt-4">
      <h2 className="mb-3">Search Hotels</h2>

      <div className="row mb-3">
        <div className="col-md-4 mb-2">
          <input
            type="text"
            className="form-control"
            placeholder="Location"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
          />
        </div>

        <div className="col-md-4 mb-2">
          <input
            type="date"
            className="form-control"
            value={checkinDate}
            onChange={(e) => setCheckinDate(e.target.value)}
          />
        </div>

        <div className="col-md-4 mb-2">
          <input
            type="number"
            className="form-control"
            placeholder="Min Price"
            value={minPrice}
            onChange={(e) => setMinPrice(e.target.value)}
          />
        </div>

        <div className="col-md-4 mb-2">
          <input
            type="number"
            className="form-control"
            placeholder="Max Price"
            value={maxPrice}
            onChange={(e) => setMaxPrice(e.target.value)}
          />
        </div>

        <div className="col-md-4 mb-2">
          <select
            className="form-select"
            value={rating}
            onChange={(e) => setRating(e.target.value)}
          >
            <option value="">Min Rating</option>
            <option value="1">1 ⭐</option>
            <option value="2">2 ⭐</option>
            <option value="3">3 ⭐</option>
            <option value="4">4 ⭐</option>
            <option value="5">5 ⭐</option>
          </select>
        </div>

        <div className="col-md-4 mb-2">
          <input
            type="text"
            className="form-control"
            placeholder="Amenities (comma separated)"
            value={amenities}
            onChange={(e) => setAmenities(e.target.value)}
          />
        </div>

        <div className="col-md-4 mb-2">
          <select
            className="form-select"
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
          >
            <option value="">Sort By</option>
            <option value="priceAsc">Price: Low to High</option>
            <option value="priceDesc">Price: High to Low</option>
            <option value="rating">Top Rated</option>
          </select>
        </div>

        <div className="col-md-4 mb-2">
          <button className="btn btn-primary w-100" onClick={handleSearch}>
            Search
          </button>
        </div>
      </div>

      {error && <div className="alert alert-danger">{error}</div>}
      {success && <div className="alert alert-success">{success}</div>}

      <div className="row">
        {hotels.length === 0 && !error && (
          <div className="text-center text-muted mt-3">No hotels to display</div>
        )}
        {hotels.map((hotel) => (
          <div key={hotel._id} className="col-md-4 mb-4">
            <div className="card shadow">
              <div className="card-body">
                <h5 className="card-title">{hotel.name}</h5>
                <p>Rating: {hotel.rating} ⭐</p>
                <p>Price: ₹{hotel.price} per night</p>
                <p>Amenities: {hotel.amenities.join(', ')}</p>
                <button className="btn btn-success" onClick={() => handleBook(hotel)}>
                  Book Now
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Hotels;
