// src/pages/UserBookings.js
import axios from 'axios';
import { useEffect, useState } from 'react';

const UserBookings = () => {
  const [bookings, setBookings] = useState([]);
  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const res = await axios.get('/api/bookings/my', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setBookings(res.data);
      } catch (err) {
        console.error('Error fetching bookings:', err);
      }
    };

    fetchBookings();
  }, []);

  const cancelBooking = async (id) => {
    if (!window.confirm('Are you sure you want to cancel this booking?')) return;

    try {
      await axios.delete(`/api/bookings/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setBookings(bookings.filter((b) => b._id !== id));
    } catch (err) {
      console.error('Error cancelling booking:', err);
    }
  };

  return (
    <div className="container mt-4">
      <h2 className="mb-4">üßæ Your Bookings</h2>
      {bookings.length === 0 ? (
        <p>No bookings found.</p>
      ) : (
        bookings.map((booking) => (
          <div key={booking._id} className="card mb-3">
            <div className="card-body">
              <h5 className="card-title">
                {booking.type === 'flight' ? (
                  <>
                    ‚úàÔ∏è <strong>{booking.flight?.airline}</strong> ‚Äî {booking.flight?.source} ‚Üí {booking.flight?.destination}
                  </>
                ) : (
                  <>
                    üè® <strong>{booking.hotel?.name}</strong> ‚Äî {booking.hotel?.location}
                  </>
                )}
              </h5>
              <p className="card-text">
                <strong>Booking ID:</strong> {booking._id} <br />
                <strong>Price:</strong> ‚Çπ{booking.totalPrice} <br />
                <strong>Booked on:</strong> {new Date(booking.createdAt).toLocaleString()}
              </p>
              <button className="btn btn-danger" onClick={() => cancelBooking(booking._id)}>
                Cancel Booking
              </button>
            </div>
          </div>
        ))
      )}
    </div>
  );
};

export default UserBookings;
