import React, { useEffect, useState } from 'react';
import axios from 'axios';

const UserBookings = () => {
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const response = await axios.get('/api/bookings/my-bookings', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        setBookings(response.data);
      } catch (error) {
        console.error('Error fetching bookings:', error);
      }
    };

    fetchBookings();
  }, []);

  return (
    <div>
      <h2>Your Bookings</h2>
      {bookings.length > 0 ? (
        bookings.map((booking) => (
          <div key={booking._id} className="card mb-3">
            <div className="card-body">
              <h5 className="card-title">Flight: {booking.flight?.airline}</h5>
              <p>From: {booking.flight?.source} → To: {booking.flight?.destination}</p>
              <p>Seats: {booking.seats}</p>
              <p>Total Price: ₹{booking.totalPrice}</p>
              <p>Booking Date: {new Date(booking.createdAt).toLocaleDateString()}</p>
            </div>
          </div>
        ))
      ) : (
        <p>No bookings found.</p>
      )}
    </div>
  );
};

export default UserBookings;
