import React, { useState } from 'react';
import axios from 'axios';
import { Form, Button, Alert } from 'react-bootstrap';

export default function Hotels() {
  const [location, setLocation] = useState('');
  const [checkIn, setCheckIn] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [hotels, setHotels] = useState([]);

  const fetchHotels = async () => {
    try {
      const filters = {};
      if (location) filters.location = location;
      if (checkIn) filters.checkIn = checkIn;

      const { data } = await axios.get('/api/hotels/search', { params: filters });
      setHotels(data);
      setError('');
    } catch (err) {
      setError('Failed to load hotels.');
      setHotels([]);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetchHotels();
  };

  const handleBook = async (hotel) => {
    const token = localStorage.getItem('authToken');
    if (!token) {
      setError('You must be logged in to book a hotel.');
      return;
    }

    try {
      const { data } = await axios.post('/api/bookings/hotel', {
        hotel: hotel._id,
        totalPrice: hotel.pricePerNight,
      }, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setSuccess('Hotel booked successfully!');
      setError('');
    } catch (err) {
      setError('Hotel booking failed.');
    }
  };

  return (
    <div className="p-4">
      <h3>Available Hotels</h3>
      <Form onSubmit={handleSubmit} className="mb-4">
        <Form.Control
          type="text"
          placeholder="Enter Location"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          className="mb-2"
        />
        <Form.Control
          type="date"
          value={checkIn}
          onChange={(e) => setCheckIn(e.target.value)}
          className="mb-2"
        />
        <Button type="submit" variant="primary">Search Hotels</Button>
      </Form>

      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">{success}</Alert>}

      {hotels.length > 0 ? (
        hotels.map(hotel => (
          <div key={hotel._id} className="border rounded p-3 mb-3">
            <h5>{hotel.name}</h5>
            <p>Location: {hotel.location}</p>
            <p>Rating: {hotel.starRating} ⭐</p>
            <p>Price: ₹{hotel.pricePerNight} per night</p>
            <p>Amenities: {hotel.amenities.join(', ')}</p>
            <Button onClick={() => handleBook(hotel)} variant="success">Book Now</Button>
          </div>
        ))
      ) : (
        <p>No hotels found for selected filters.</p>
      )}
    </div>
  );
}
