import { useState } from 'react';
import axios from 'axios';
import { Form, Button, Alert } from 'react-bootstrap';

export default function Flights() {
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [date, setDate] = useState('');
  const [error, setError] = useState('');
  const [flights, setFlights] = useState([]);

  const fetchFlights = async (formattedDate) => {
    try {
      const { data } = await axios.get('/api/flights/search', {
        params: { from, to, date: formattedDate },
      });
      setFlights(data);
      setError('');
    } catch (err) {
      setError('Failed to fetch flights.');
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const formattedDate = new Date(date).toISOString().split('T')[0];
    console.log('Formatted Date:', formattedDate); // Log the formatted date

    fetchFlights(formattedDate);
  };

  return (
    <div>
      <Form onSubmit={handleSubmit}>
        <Form.Control
          type="text"
          placeholder="From"
          value={from}
          onChange={(e) => setFrom(e.target.value)}
          required
        />
        <Form.Control
          type="text"
          placeholder="To"
          value={to}
          onChange={(e) => setTo(e.target.value)}
          required
        />
        <Form.Control
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          required
        />
        <Button type="submit">Search Flights</Button>
      </Form>

      {error && <Alert variant="danger">{error}</Alert>}

      <div>
        {flights.length > 0 ? (
          flights.map((flight) => (
            <div key={flight._id}>
              <p>{flight.from} to {flight.to}</p>
            </div>
          ))
        ) : (
          <p>No flights found for the selected date.</p>
        )}
      </div>
    </div>
  );
}
