import { useState } from 'react';
import axios from 'axios';
import { Form, Button, Alert } from 'react-bootstrap';

// Mapping IATA codes to full city names
const cityMap = {
  DEL: 'Delhi',
  BOM: 'Mumbai',
  BLR: 'Bangalore',
  HYD: 'Hyderabad',
  PNQ: 'Pune',
  MAA: 'Chennai',
  CCU: 'Kolkata',
  GOI: 'Goa',
  // add more as needed
};

export default function Flights() {
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [date, setDate] = useState('');
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [flights, setFlights] = useState([]);

  const fetchFlights = async (formattedDate) => {
    try {
      const filters = {
        source: from.trim().toUpperCase(),
        destination: to.trim().toUpperCase(),
        date: formattedDate,
      };

      if (minPrice !== '') filters.minPrice = minPrice;
      if (maxPrice !== '') filters.maxPrice = maxPrice;

      const { data } = await axios.get('/api/flights/search', { params: filters });
      setFlights(data);
      setError('');
    } catch (err) {
      console.error(err);
      setError('Failed to fetch flights.');
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const formattedDate = new Date(date).toISOString().split('T')[0];
    fetchFlights(formattedDate);
  };

  const handleBook = async (flight) => {
    const token = localStorage.getItem('token');
    if (!token) {
      setError('You must be logged in to book a flight.');
      return;
    }

    try {
      const bookingData = {
        flight: flight._id,
        seats: 1,
        totalPrice: flight.price,
      };

      const { data } = await axios.post(
        '/api/bookings/flight',
        bookingData,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      setSuccess('Booking successful!');
      setError('');
      console.log(data);
    } catch (err) {
      console.error(err);
      setError(err.response?.data?.message || 'Failed to book flight.');
    }
  };

  return (
    <div className="p-4">
      <h3>Search Flights</h3>
      <Form onSubmit={handleSubmit} className="mb-4">
        <Form.Control
          type="text"
          placeholder="From (e.g., DEL)"
          value={from}
          onChange={(e) => setFrom(e.target.value)}
          required
          className="mb-2"
        />
        <Form.Control
          type="text"
          placeholder="To (e.g., BOM)"
          value={to}
          onChange={(e) => setTo(e.target.value)}
          required
          className="mb-2"
        />
        <Form.Control
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          required
          className="mb-2"
        />
        <Form.Control
          type="number"
          placeholder="Min Price"
          value={minPrice}
          onChange={(e) => setMinPrice(e.target.value)}
          className="mb-2"
        />
        <Form.Control
          type="number"
          placeholder="Max Price"
          value={maxPrice}
          onChange={(e) => setMaxPrice(e.target.value)}
          className="mb-2"
        />
        <Button type="submit" variant="primary">Search Flights</Button>
      </Form>

      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">{success}</Alert>}

      {flights.length > 0 ? (
        flights.map((flight) => (
          <div key={flight._id} className="border rounded p-3 mb-3">
            <h5>{flight.airline}</h5>
            <p>
              {cityMap[flight.from] || flight.from} ({flight.from}) ➡ {cityMap[flight.to] || flight.to} ({flight.to})
            </p>
            <p>Departure: {new Date(flight.departureDate).toDateString()}</p>
            <p>Price: ₹{flight.price}</p>
            <Button onClick={() => handleBook(flight)} variant="success">Book Now</Button>
          </div>
        ))
      ) : (
        <p>No flights found for the selected filters.</p>
      )}
    </div>
  );
}
