import { useEffect, useState } from 'react';
import axios from 'axios';
import { Button, Modal, Form, Table } from 'react-bootstrap';

const ManageHotels = () => {
  const [hotels, setHotels] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [editHotel, setEditHotel] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    location: '',
    price: '',
    rating: '',
    amenities: ''
  });

  const token = localStorage.getItem('adminToken');

  const fetchHotels = async () => {
    try {
      const res = await axios.get('/api/admin/hotels', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setHotels(res.data);
    } catch (err) {
      console.error('Error fetching hotels:', err);
    }
  };

  useEffect(() => {
    fetchHotels();
  }, []);

  const handleShow = (hotel = null) => {
    setEditHotel(hotel);
    setFormData(
      hotel || {
        name: '',
        location: '',
        price: '',
        rating: '',
        amenities: ''
      }
    );
    setShowModal(true);
  };

  const handleClose = () => {
    setShowModal(false);
    setEditHotel(null);
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async () => {
    try {
      const data = {
        ...formData,
        price: parseInt(formData.price),
        rating: parseFloat(formData.rating),
        amenities: formData.amenities.split(',').map((a) => a.trim())
      };

      if (editHotel) {
        await axios.put(`/api/admin/hotels/${editHotel._id}`, data, {
          headers: { Authorization: `Bearer ${token}` }
        });
      } else {
        await axios.post('/api/admin/hotels', data, {
          headers: { Authorization: `Bearer ${token}` }
        });
      }
      handleClose();
      fetchHotels();
    } catch (err) {
      console.error('Error saving hotel:', err);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this hotel?')) {
      try {
        await axios.delete(`/api/admin/hotels/${id}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        fetchHotels();
      } catch (err) {
        console.error('Error deleting hotel:', err);
      }
    }
  };

  return (
    <div className="container mt-4">
      <h2>Manage Hotels</h2>
      <Button onClick={() => handleShow()} className="mb-3">Add Hotel</Button>

      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Name</th>
            <th>Location</th>
            <th>Price (per night)</th>
            <th>Rating</th>
            <th>Amenities</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {hotels.map((hotel) => (
            <tr key={hotel._id}>
              <td>{hotel.name}</td>
              <td>{hotel.location}</td>
              <td>â‚¹{hotel.price}</td>
              <td>{hotel.rating}</td>
              <td>{hotel.amenities?.join(', ')}</td>
              <td>
                <Button
                  variant="warning"
                  size="sm"
                  onClick={() => handleShow(hotel)}
                  className="me-2"
                >
                  Edit
                </Button>
                <Button
                  variant="danger"
                  size="sm"
                  onClick={() => handleDelete(hotel._id)}
                >
                  Delete
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      {/* Modal for Add/Edit */}
      <Modal show={showModal} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>{editHotel ? 'Edit Hotel' : 'Add Hotel'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-2">
              <Form.Label>Name</Form.Label>
              <Form.Control name="name" value={formData.name} onChange={handleChange} />
            </Form.Group>

            <Form.Group className="mb-2">
              <Form.Label>Location</Form.Label>
              <Form.Control name="location" value={formData.location} onChange={handleChange} />
            </Form.Group>

            <Form.Group className="mb-2">
              <Form.Label>Price (INR)</Form.Label>
              <Form.Control name="price" type="number" value={formData.price} onChange={handleChange} />
            </Form.Group>

            <Form.Group className="mb-2">
              <Form.Label>Rating</Form.Label>
              <Form.Control name="rating" type="number" step="0.1" value={formData.rating} onChange={handleChange} />
            </Form.Group>

            <Form.Group className="mb-2">
              <Form.Label>Amenities (comma-separated)</Form.Label>
              <Form.Control name="amenities" value={formData.amenities} onChange={handleChange} />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>Cancel</Button>
          <Button variant="primary" onClick={handleSubmit}>
            {editHotel ? 'Update' : 'Add'}
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default ManageHotels;
