// src/pages/Flights.js
import { useEffect, useState } from 'react';
import axios from 'axios';
import { Button, Form, Alert } from 'react-bootstrap';
import 'bootstrap-icons/font/bootstrap-icons.css';

const Flights = () => {
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [date, setDate] = useState('');
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');
  const [flights, setFlights] = useState([]); // ‚úÖ always an array
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const token = localStorage.getItem('token');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      const res = await axios.get('/api/flights/search', {
        params: { source: from, destination: to, departureDate: date, minPrice, maxPrice },
      });

      if (res.data.length === 0) {
        setError('No flights found.');
      } else {
        setFlights(res.data);
        setSuccess(`${res.data.length} flights found.`);
      }
    } catch (err) {
      console.error('Error fetching flights:', err);
      setError('Error fetching flights. Try again.');
    }
  };

  const handleBook = async (flight) => {
    if (!token) {
      setError('Please login to book a flight.');
      return;
    }

    try {
      await axios.post(
        '/api/bookings/flight',
        { flightId: flight._id },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setSuccess(`Successfully booked flight with ${flight.airline}!`);
    } catch (err) {
      console.error('Booking error:', err);
      setError('Booking failed. Try again.');
    }
  };

  return (
    <div className="container my-5">
      <h2 className="text-primary mb-4">
        <i className="bi bi-airplane-engines"></i> Search Flights
      </h2>

      <Form onSubmit={handleSubmit} className="row g-3">
        <div className="col-md-6">
          <Form.Control
            placeholder="From (e.g., Delhi)"
            value={from}
            onChange={(e) => setFrom(e.target.value)}
            required
          />
        </div>
        <div className="col-md-6">
          <Form.Control
            placeholder="To (e.g., Mumbai)"
            value={to}
            onChange={(e) => setTo(e.target.value)}
            required
          />
        </div>
        <div className="col-md-4">
          <Form.Control
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
          />
        </div>
        <div className="col-md-4">
          <Form.Control
            placeholder="Min Price"
            type="number"
            value={minPrice}
            onChange={(e) => setMinPrice(e.target.value)}
          />
        </div>
        <div className="col-md-4">
          <Form.Control
            placeholder="Max Price"
            type="number"
            value={maxPrice}
            onChange={(e) => setMaxPrice(e.target.value)}
          />
        </div>
        <div className="col-12 d-grid">
          <Button type="submit" variant="primary">
            üîç Search Flights
          </Button>
        </div>
      </Form>

      {error && <Alert variant="danger" className="mt-3">{error}</Alert>}
      {success && <Alert variant="success" className="mt-3">{success}</Alert>}

      <div className="mt-4">
        {Array.isArray(flights) && flights.length > 0 ? (
          flights.map((flight) => (
            <div key={flight._id} className="card shadow-sm mb-3 border-info">
              <div className="card-body">
                <h5 className="card-title text-info">
                  ‚úàÔ∏è {flight.airline} ‚Äî {flight.source} ‚û° {flight.destination}
                </h5>
                <p className="card-text mb-2">
                  üïì Departure: <strong>{new Date(flight.departureDate).toDateString()}</strong> <br />
                  üí∏ Price: <strong>‚Çπ{flight.price}</strong>
                </p>
                <Button variant="success" onClick={() => handleBook(flight)}>
                  ‚úÖ Book Now
                </Button>
              </div>
            </div>
          ))
        ) : (
          <p className="text-muted">No flights to display.</p>
        )}
      </div>
    </div>
  );
};

export default Flights;
