import React, { useEffect, useState } from "react";
import axios from "axios";
import { Button, Table, Modal, Form } from "react-bootstrap";

const ManageFlights = () => {
  const [flights, setFlights] = useState([]);
  const [show, setShow] = useState(false);
  const [formData, setFormData] = useState({
    airline: "",
    from: "",
    to: "",
    departureDate: "",
    departureTime: "",
    arrivalTime: "",
    price: "",
  });

  const [editId, setEditId] = useState(null);

  // ðŸ•’ Format time to AM/PM
  const formatTimeAMPM = (timeStr) => {
    if (!timeStr) return "";
    const [hour, minute] = timeStr.split(":");
    const h = parseInt(hour, 10);
    const suffix = h >= 12 ? "PM" : "AM";
    const adjustedHour = h % 12 || 12;
    return `${adjustedHour}:${minute} ${suffix}`;
  };

  const fetchFlights = async () => {
    try {
      const res = await axios.get("/api/admin/flights", {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setFlights(res.data);
    } catch (err) {
      console.error("Fetch Error:", err.message);
    }
  };

  useEffect(() => {
    fetchFlights();
  }, []);

  const handleClose = () => {
    setShow(false);
    setFormData({
      airline: "",
      from: "",
      to: "",
      departureDate: "",
      departureTime: "",
      arrivalTime: "",
      price: "",
    });
    setEditId(null);
  };

  const handleShow = (flight = null) => {
    if (flight) {
      setFormData(flight);
      setEditId(flight._id);
    }
    setShow(true);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async () => {
    try {
      const token = localStorage.getItem("token");
      if (editId) {
        await axios.put(`/api/admin/flights/${editId}`, formData, {
          headers: { Authorization: `Bearer ${token}` },
        });
      } else {
        await axios.post("/api/admin/flights", formData, {
          headers: { Authorization: `Bearer ${token}` },
        });
      }
      fetchFlights();
      handleClose();
    } catch (err) {
      console.error("Submit Error:", err.message);
      alert("Failed to save flight. Check console for details.");
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`/api/admin/flights/${id}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      fetchFlights();
    } catch (err) {
      console.error("Delete Error:", err.message);
    }
  };

  return (
    <div className="container mt-4">
      <h2 className="mb-4 text-primary">Manage Flights</h2>
      <Button variant="primary" className="mb-3" onClick={() => handleShow()}>
        Add Flight
      </Button>

      <Table striped bordered hover responsive>
        <thead>
          <tr>
            <th>Airline</th>
            <th>Source</th>
            <th>Destination</th>
            <th>Departure</th>
            <th>Arrival</th>
            <th>Date</th>
            <th>Price</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {flights.map((flight) => (
            <tr key={flight._id}>
              <td>{flight.airline}</td>
              <td>{flight.from}</td>
              <td>{flight.to}</td>
              <td>{formatTimeAMPM(flight.departureTime)}</td>
              <td>{formatTimeAMPM(flight.arrivalTime)}</td>
              <td>
                {new Date(flight.departureDate).toLocaleDateString("en-GB", {
                  day: "2-digit",
                  month: "short",
                  year: "numeric",
                })}
              </td>
              <td>â‚¹{flight.price}</td>
              <td>
                <Button
                  variant="warning"
                  size="sm"
                  onClick={() => handleShow(flight)}
                  className="me-2"
                >
                  Edit
                </Button>
                <Button
                  variant="danger"
                  size="sm"
                  onClick={() => handleDelete(flight._id)}
                >
                  Delete
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      {/* Modal Form */}
      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>{editId ? "Edit Flight" : "Add Flight"}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="airline">
              <Form.Label>Airline</Form.Label>
              <Form.Control
                type="text"
                name="airline"
                value={formData.airline}
                onChange={handleChange}
                required
              />
            </Form.Group>

            <Form.Group controlId="from">
              <Form.Label>Source</Form.Label>
              <Form.Control
                type="text"
                name="from"
                value={formData.from}
                onChange={handleChange}
                required
              />
            </Form.Group>

            <Form.Group controlId="to">
              <Form.Label>Destination</Form.Label>
              <Form.Control
                type="text"
                name="to"
                value={formData.to}
                onChange={handleChange}
                required
              />
            </Form.Group>

            <Form.Group controlId="departureDate">
              <Form.Label>Departure Date</Form.Label>
              <Form.Control
                type="date"
                name="departureDate"
                value={formData.departureDate}
                onChange={handleChange}
                required
              />
            </Form.Group>

            <Form.Group controlId="departureTime">
              <Form.Label>Departure Time</Form.Label>
              <Form.Control
                type="time"
                name="departureTime"
                value={formData.departureTime}
                onChange={handleChange}
                required
              />
            </Form.Group>

            <Form.Group controlId="arrivalTime">
              <Form.Label>Arrival Time</Form.Label>
              <Form.Control
                type="time"
                name="arrivalTime"
                value={formData.arrivalTime}
                onChange={handleChange}
                required
              />
            </Form.Group>

            <Form.Group controlId="price">
              <Form.Label>Price (â‚¹)</Form.Label>
              <Form.Control
                type="number"
                name="price"
                value={formData.price}
                onChange={handleChange}
                required
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Cancel
          </Button>
          <Button variant="primary" onClick={handleSubmit}>
            {editId ? "Update" : "Add"}
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default ManageFlights;
