import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Hotels = () => {
  const [hotels, setHotels] = useState([]);
  const [location, setLocation] = useState('');
  const [checkinDate, setCheckinDate] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleSearch = async () => {
    try {
      const res = await axios.get('/api/hotels/search', {
        params: {
          location,
          checkinDate,
        },
      });
      setHotels(res.data);
      setError('');
      setSuccess('');
    } catch (err) {
      setError('Failed to load hotels.');
      setHotels([]);
    }
  };

  const handleBook = async (hotel) => {
    const token = localStorage.getItem('authToken');
    if (!token) {
      setError('You must be logged in to book a hotel.');
      return;
    }

    try {
      const { data } = await axios.post('/api/bookings/hotel', {
        hotel: hotel._id,
        totalPrice: hotel.pricePerNight,
      }, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setSuccess('Hotel booked successfully!');
    } catch (err) {
      setError('Hotel booking failed.');
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Available Hotels</h2>
      <input
        type="text"
        placeholder="Location"
        value={location}
        onChange={(e) => setLocation(e.target.value)}
      />
      <input
        type="date"
        value={checkinDate}
        onChange={(e) => setCheckinDate(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>

      {error && <p style={{ color: 'red' }}>{error}</p>}
      {success && <p style={{ color: 'green' }}>{success}</p>}

      <div>
        {hotels.map((hotel) => (
          <div key={hotel._id} style={{ border: '1px solid #ccc', margin: '10px', padding: '10px' }}>
            <h3>{hotel.name}</h3>
            <p><strong>Location:</strong> {hotel.location}</p>
            <p><strong>Price:</strong> ₹{hotel.pricePerNight} per night</p>
            <p><strong>Rating:</strong> {hotel.rating} ⭐</p>
            <p><strong>Amenities:</strong> {hotel.amenities.join(', ')}</p>
            <button onClick={() => handleBook(hotel)}>Book Now</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Hotels;
