import { useState } from 'react';
import axios from 'axios';
import { Form, Button, Alert } from 'react-bootstrap';

export default function Flights() {
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [date, setDate] = useState('');
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');
  const [error, setError] = useState('');
  const [flights, setFlights] = useState([]);

  // Function to fetch flights with filters
  const fetchFlights = async (formattedDate) => {
    try {
      // Prepare the query params to send to the backend
      const filters = {
        source: from,
        destination: to,
        date: formattedDate,
      };

      // Add minPrice and maxPrice only if they are provided
      if (minPrice !== '') filters.minPrice = minPrice;
      if (maxPrice !== '') filters.maxPrice = maxPrice;

      const { data } = await axios.get('/api/flights/search', { params: filters });
      setFlights(data);
      setError('');
    } catch (err) {
      setError('Failed to fetch flights.');
    }
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    const formattedDate = new Date(date).toISOString().split('T')[0]; // Format the date
    fetchFlights(formattedDate); // Fetch flights with formatted date
  };

  return (
    <div className="p-4">
      <h3>Search Flights</h3>
      <Form onSubmit={handleSubmit} className="mb-4">
        <Form.Control
          type="text"
          placeholder="From"
          value={from}
          onChange={(e) => setFrom(e.target.value)}
          required
          className="mb-2"
        />
        <Form.Control
          type="text"
          placeholder="To"
          value={to}
          onChange={(e) => setTo(e.target.value)}
          required
          className="mb-2"
        />
        <Form.Control
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          required
          className="mb-2"
        />
        <Form.Control
          type="number"
          placeholder="Min Price"
          value={minPrice}
          onChange={(e) => setMinPrice(e.target.value)}
          className="mb-2"
        />
        <Form.Control
          type="number"
          placeholder="Max Price"
          value={maxPrice}
          onChange={(e) => setMaxPrice(e.target.value)}
          className="mb-2"
        />
        <Button type="submit" variant="primary">Search Flights</Button>
      </Form>

      {error && <Alert variant="danger">{error}</Alert>}

      {flights.length > 0 ? (
        flights.map((flight) => (
          <div key={flight._id} className="border rounded p-3 mb-3">
            <h5>{flight.airline}</h5>
            <p>{flight.source} ➡ {flight.destination}</p>
            <p>Departure: {new Date(flight.departureDate).toDateString()}</p>
            <p>Price: ₹{flight.price}</p>
          </div>
        ))
      ) : (
        <p>No flights found for the selected filters.</p>
      )}
    </div>
  );
}
