import { useState } from 'react';
import { Form, Button, Alert } from 'react-bootstrap';
import 'bootstrap-icons/font/bootstrap-icons.css';

const Flights = ({ flights, handleBook, error, success }) => {
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [date, setDate] = useState('');
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Your fetch logic
  };

  return (
    <div className="container my-5">
      <h2 className="text-primary mb-4">
        <i className="bi bi-airplane-engines"></i> Search Flights
      </h2>

      <Form onSubmit={handleSubmit} className="row g-3">
        <div className="col-md-6">
          <Form.Control placeholder="From (e.g., Delhi)" value={from} onChange={(e) => setFrom(e.target.value)} required />
        </div>
        <div className="col-md-6">
          <Form.Control placeholder="To (e.g., Mumbai)" value={to} onChange={(e) => setTo(e.target.value)} required />
        </div>
        <div className="col-md-4">
          <Form.Control type="date" value={date} onChange={(e) => setDate(e.target.value)} required />
        </div>
        <div className="col-md-4">
          <Form.Control type="number" placeholder="Min Price" value={minPrice} onChange={(e) => setMinPrice(e.target.value)} />
        </div>
        <div className="col-md-4">
          <Form.Control type="number" placeholder="Max Price" value={maxPrice} onChange={(e) => setMaxPrice(e.target.value)} />
        </div>
        <div className="col-12 d-grid">
          <Button type="submit" variant="primary">üîç Search Flights</Button>
        </div>
      </Form>

      {error && <Alert variant="danger" className="mt-3">{error}</Alert>}
      {success && <Alert variant="success" className="mt-3">{success}</Alert>}

      <div className="mt-4">
        {flights.length > 0 ? (
          flights.map(flight => (
            <div className="card mb-3 shadow-sm" key={flight._id}>
              <div className="card-body">
                <h5 className="card-title text-info">
                  ‚úàÔ∏è {flight.airline} ‚Äî {flight.source} ‚û° {flight.destination}
                </h5>
                <p className="card-text">
                  üïì Departure: {new Date(flight.departureDate).toDateString()} <br />
                  üí∏ Price: ‚Çπ{flight.price}
                </p>
                <Button variant="success" onClick={() => handleBook(flight)}>Book Now</Button>
              </div>
            </div>
          ))
        ) : (
          <p className="text-muted">No flights found for the selected filters.</p>
        )}
      </div>
    </div>
  );
};

export default Flights;
