import React, { useState } from 'react';
import axios from 'axios';

function Hotels() {
  const [city, setCity] = useState('');
  const [date, setDate] = useState('');
  const [hotels, setHotels] = useState([]);

  const searchHotels = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/hotels/search', {
        params: { city, date },
      });
      setHotels(res.data);
    } catch (err) {
      console.error('Error fetching hotels:', err);
    }
  };

  return (
    <div>
      <h2>Search Hotels</h2>
      <input type="text" placeholder="City" value={city} onChange={(e) => setCity(e.target.value)} />
      <input type="date" value={date} onChange={(e) => setDate(e.target.value)} />
      <button onClick={searchHotels}>Search</button>

      {hotels.map((hotel, idx) => (
        <div key={idx} style={{ marginTop: '10px', padding: '10px', border: '1px solid #ccc' }}>
          <h4>{hotel.name}</h4>
          <p>City: {hotel.city}</p>
          <p>Available: {hotel.availableFrom} to {hotel.availableTo}</p>
          <p>Price: ₹{hotel.pricePerNight} / night</p>
          <p>Rating: {hotel.stars} ⭐</p>
        </div>
      ))}
    </div>
  );
}

export default Hotels;
