import { useEffect, useState } from 'react';
import axios from 'axios';
import { Button, Modal, Form, Table } from 'react-bootstrap';

const ManageFlights = () => {
  const [flights, setFlights] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [editFlight, setEditFlight] = useState(null);
  const [formData, setFormData] = useState({
    airline: '',
    from: '',
    to: '',
    departureTime: '',
    arrivalTime: '',
    departureDate: '',
    price: ''
  });

  const token = localStorage.getItem('adminToken');

  const fetchFlights = async () => {
    try {
      const res = await axios.get('/api/admin/flights', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setFlights(res.data);
    } catch (err) {
      console.error('Error fetching flights:', err);
    }
  };

  useEffect(() => {
    fetchFlights();
  }, []);

  const handleShow = (flight = null) => {
    setEditFlight(flight);
    setFormData(
      flight || {
        airline: '',
        from: '',
        to: '',
        departureTime: '',
        arrivalTime: '',
        departureDate: '',
        price: ''
      }
    );
    setShowModal(true);
  };

  const handleClose = () => {
    setShowModal(false);
    setEditFlight(null);
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async () => {
    try {
      if (editFlight) {
        await axios.put(`/api/admin/flights/${editFlight._id}`, formData, {
          headers: { Authorization: `Bearer ${token}` }
        });
      } else {
        await axios.post('/api/admin/flights', formData, {
          headers: { Authorization: `Bearer ${token}` }
        });
      }
      handleClose();
      fetchFlights();
    } catch (err) {
      console.error('Error saving flight:', err);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this flight?')) {
      try {
        await axios.delete(`/api/admin/flights/${id}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        fetchFlights();
      } catch (err) {
        console.error('Error deleting flight:', err);
      }
    }
  };

  // 🟦 Format date function
  const formatDateTime = (isoString) => {
    return new Date(isoString).toLocaleString('en-IN', {
      day: '2-digit',
      month: 'short',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
      hour12: true
    });
  };

  return (
    <div className="container mt-4">
      <h2>Manage Flights</h2>
      <Button onClick={() => handleShow()} className="mb-3">Add Flight</Button>

      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Airline</th>
            <th>Source</th>
            <th>Destination</th>
            <th>Departure</th>
            <th>Arrival</th>
            <th>Date</th>
            <th>Price</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {flights.map((flight) => (
            <tr key={flight._id}>
              <td>{flight.airline}</td>
              <td>{flight.from}</td>
              <td>{flight.to}</td>
              <td>{flight.departureTime}</td>
              <td>{flight.arrivalTime}</td>
              <td>{formatDateTime(flight.departureDate)}</td> {/* ⬅️ Updated */}
              <td>₹{flight.price}</td>
              <td>
                <Button
                  variant="warning"
                  size="sm"
                  onClick={() => handleShow(flight)}
                  className="me-2"
                >
                  Edit
                </Button>
                <Button
                  variant="danger"
                  size="sm"
                  onClick={() => handleDelete(flight._id)}
                >
                  Delete
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      {/* Modal for Add/Edit */}
      <Modal show={showModal} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>{editFlight ? 'Edit Flight' : 'Add Flight'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            {['airline', 'from', 'to', 'departureTime', 'arrivalTime', 'departureDate', 'price'].map((field) => (
              <Form.Group key={field} className="mb-3">
                <Form.Label>
                  {field === 'from'
                    ? 'Source'
                    : field === 'to'
                    ? 'Destination'
                    : field.charAt(0).toUpperCase() + field.slice(1)}
                </Form.Label>
                <Form.Control
                  type={field === 'price' ? 'number' : 'text'}
                  name={field}
                  value={formData[field]}
                  onChange={handleChange}
                />
              </Form.Group>
            ))}
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>Cancel</Button>
          <Button variant="primary" onClick={handleSubmit}>
            {editFlight ? 'Update' : 'Add'}
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default ManageFlights;
