import { useState } from 'react';
import axios from 'axios';
import { Form, Button, Alert } from 'react-bootstrap';

export default function Hotels() {
  const [location, setLocation] = useState('');
  const [date, setDate] = useState('');
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');
  const [hotels, setHotels] = useState([]);
  const [error, setError] = useState('');

  const fetchHotels = async (formattedDate) => {
    try {
      const filters = {
        location,
        date: formattedDate,
        minPrice: minPrice ? parseFloat(minPrice) : 0,
        maxPrice: maxPrice ? parseFloat(maxPrice) : 999999,
      };

      const { data } = await axios.get('/api/flights/hotels/search', { params: filters });
      setHotels(data);
      setError('');
    } catch (err) {
      console.error('❌ Hotel fetch error:', err.message);
      setError('Failed to fetch hotels.');
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const formattedDate = new Date(date).toISOString().split('T')[0];
    fetchHotels(formattedDate);
  };

  return (
    <div className="p-4">
      <h3>Search Hotels</h3>
      <Form onSubmit={handleSubmit} className="mb-4">
        <Form.Control
          type="text"
          placeholder="Location"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          required
          className="mb-2"
        />
        <Form.Control
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          required
          className="mb-2"
        />
        <Form.Control
          type="number"
          placeholder="Min Price"
          value={minPrice}
          onChange={(e) => setMinPrice(e.target.value)}
          className="mb-2"
        />
        <Form.Control
          type="number"
          placeholder="Max Price"
          value={maxPrice}
          onChange={(e) => setMaxPrice(e.target.value)}
          className="mb-2"
        />
        <Button type="submit" variant="primary">Search Hotels</Button>
      </Form>

      {error && <Alert variant="danger">{error}</Alert>}

      {hotels.length > 0 ? (
        hotels.map((hotel) => (
          <div key={hotel._id} className="border rounded p-3 mb-3">
            <h5>{hotel.name}</h5>
            <p>City: {hotel.location}</p>
            <p>Available: {hotel.checkinDate ? new Date(hotel.checkinDate).toDateString() : 'N/A'}</p>
            <p>Price: ₹{hotel.pricePerNight} / night</p>
            <p>Rating: {hotel.rating} ⭐</p>
          </div>
        ))
      ) : (
        <p>No hotels found for the selected filters.</p>
      )}
    </div>
  );
}
