import { useEffect, useState } from 'react';
import axios from 'axios';

const ManageHotels = () => {
  const [hotels, setHotels] = useState([]);
  const token = localStorage.getItem('adminToken');

  useEffect(() => {
    fetchHotels();
  }, []);

  const fetchHotels = async () => {
    const res = await axios.get('/api/admin/hotels', {
      headers: { Authorization: `Bearer ${token}` }
    });
    setHotels(res.data);
  };

  const deleteHotel = async (id) => {
    await axios.delete(`/api/admin/hotels/${id}`, {
      headers: { Authorization: `Bearer ${token}` }
    });
    fetchHotels();
  };

  return (
    <div className="container mt-5">
      <h2>Manage Hotels</h2>
      {hotels.map(hotel => (
        <div key={hotel._id} className="card mb-3 p-3">
          <p><strong>{hotel.name}</strong> â€” {hotel.location}</p>
          <button className="btn btn-danger" onClick={() => deleteHotel(hotel._id)}>Delete</button>
        </div>
      ))}
    </div>
  );
};

export default ManageHotels;
