import React, { useState } from 'react';
import axios from 'axios';
import { Form, Button, Alert } from 'react-bootstrap';

const Hotels = () => {
  const [checkIn, setCheckIn] = useState('');
  const [checkOut, setCheckOut] = useState('');
  const [hotels, setHotels] = useState([]);
  const [error, setError] = useState('');
  const [searched, setSearched] = useState(false); // üÜï to check if search has been attempted

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSearched(true); // üÜï set this true on form submit
    try {
      const { data } = await axios.get('/api/hotels/search', {
        params: {
          checkIn,
          checkOut,
        },
      });
      setHotels(data);
      setError('');
    } catch (err) {
      setError('Failed to load hotels.');
      setHotels([]);
    }
  };

  const handleBookHotel = async (hotel) => {
    const token = localStorage.getItem('authToken');
    if (!token) {
      alert('You must be logged in to book a hotel.');
      return;
    }

    try {
      const bookingData = {
        hotel: hotel._id,
        checkIn,
        checkOut,
        totalPrice: hotel.pricePerNight, // assuming booking 1 night
      };

      const res = await axios.post('/api/hotel-bookings', bookingData, {
        headers: { Authorization: `Bearer ${token}` },
      });

      alert('Hotel booked successfully!');
    } catch (err) {
      console.error(err);
      alert('Failed to book hotel.');
    }
  };

  return (
    <div className="p-4">
      <h3>Available Hotels</h3>

      <Form onSubmit={handleSubmit} className="mb-4">
        <Form.Control
          type="date"
          value={checkIn}
          onChange={(e) => setCheckIn(e.target.value)}
          required
          className="mb-2"
        />
        <Form.Control
          type="date"
          value={checkOut}
          onChange={(e) => setCheckOut(e.target.value)}
          required
          className="mb-2"
        />
        <Button type="submit" variant="primary">Search Hotels</Button>
      </Form>

      {/* Only show error if user has searched */}
      {searched && error && <Alert variant="danger">{error}</Alert>}

      {searched && hotels.length === 0 && !error && (
        <p>No hotels found for selected dates.</p>
      )}

      {hotels.map((hotel) => (
        <div key={hotel._id} className="border rounded p-3 mb-3">
          <h5>{hotel.name}</h5>
          <p>{hotel.location}</p>
          <p>Rating: ‚≠ê {hotel.rating}</p>
          <p>Price per night: ‚Çπ{hotel.pricePerNight}</p>
          <Button onClick={() => handleBookHotel(hotel)} variant="success">Book Now</Button>
        </div>
      ))}
    </div>
  );
};

export default Hotels;
